SET statement_timeout = 0;
SET lock_timeout = 0;
SET idle_in_transaction_session_timeout = 0;
SET client_encoding = 'UTF8';
SET standard_conforming_strings = on;
SELECT pg_catalog.set_config('search_path', '', false);
SET check_function_bodies = false;
SET xmloption = content;
SET client_min_messages = warning;
SET row_security = off;

CREATE EXTENSION IF NOT EXISTS plpgsql WITH SCHEMA pg_catalog;
COMMENT ON EXTENSION plpgsql IS 'PL/pgSQL procedural language';

CREATE EXTENSION IF NOT EXISTS postgis WITH SCHEMA public;
COMMENT ON EXTENSION postgis IS 'PostGIS geometry, geography, and raster spatial types and functions';

CREATE EXTENSION IF NOT EXISTS postgis_raster WITH SCHEMA public;

CREATE TYPE public.role AS ENUM (
    'admin',
    'user',
    'premium'
);

CREATE FUNCTION public.generate_hash() RETURNS trigger
    LANGUAGE plpgsql
AS $$
BEGIN
    IF NEW.hash IS NULL THEN
        NEW.hash := md5(CAST(NEW.id AS text) || NEW.name || CAST(NEW.cre_date AS text));
    END IF;
    RETURN NEW;
END;
$$;

CREATE FUNCTION public.generate_token() RETURNS trigger
    LANGUAGE plpgsql
AS $$
BEGIN
    IF NEW.token IS NULL THEN
        NEW.token := md5(NEW.username || NOW());
    END IF;
    RETURN NEW;
END;
$$;

SET default_tablespace = '';

SET default_with_oids = false;

CREATE TABLE public.dashboard (
    id integer NOT NULL,
    title character varying(50) NOT NULL,
    date date DEFAULT CURRENT_TIMESTAMP NOT NULL,
    item_1 character varying(100) DEFAULT NULL::character varying,
    item_2 character varying(100) DEFAULT NULL::character varying,
    item_3 character varying(100) DEFAULT NULL::character varying,
    item_4 character varying(100) DEFAULT NULL::character varying
);

CREATE SEQUENCE public.dashboard_id_seq
    AS integer
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE public.dashboard_id_seq OWNED BY public.dashboard.id;

CREATE TABLE public.pass_recovery (
    token character(64) NOT NULL,
    user_id integer NOT NULL,
    created timestamp without time zone DEFAULT CURRENT_TIMESTAMP NOT NULL
);

CREATE TABLE public.poi (
    name character varying(15) NOT NULL,
    location public.geography(Point,4326) NOT NULL,
    description character varying(100) DEFAULT NULL::character varying
);

CREATE TABLE public.tracks (
    id integer NOT NULL,
    track public.geography(LineString,4326) DEFAULT NULL::public.geography,
    user_id integer NOT NULL,
    name character varying(50) DEFAULT 'Unnamed track'::character varying NOT NULL,
    cre_date timestamp without time zone DEFAULT CURRENT_TIMESTAMP NOT NULL,
    hash character(32) NOT NULL
);

CREATE SEQUENCE public.tracks_id_seq
    AS integer
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE public.tracks_id_seq OWNED BY public.tracks.id;

CREATE TABLE public.users (
    id integer NOT NULL,
    username character varying(30) NOT NULL,
    email character varying NOT NULL,
    fb_uid character varying,
    password character(60),
    role public.role DEFAULT 'user'::public.role NOT NULL,
    reg_date timestamp without time zone DEFAULT CURRENT_TIMESTAMP NOT NULL,
    token character(32) NOT NULL,
    CONSTRAINT fborpwfilled CHECK (((
      CASE
          WHEN (fb_uid IS NULL) THEN 0
          ELSE 1
          END +
      CASE
          WHEN (password IS NULL) THEN 0
          ELSE 1
          END) > 0))
);

CREATE SEQUENCE public.users_id_seq
    AS integer
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE public.users_id_seq OWNED BY public.users.id;

ALTER TABLE ONLY public.dashboard ALTER COLUMN id SET DEFAULT nextval('public.dashboard_id_seq'::regclass);
ALTER TABLE ONLY public.tracks ALTER COLUMN id SET DEFAULT nextval('public.tracks_id_seq'::regclass);
ALTER TABLE ONLY public.users ALTER COLUMN id SET DEFAULT nextval('public.users_id_seq'::regclass);






SELECT pg_catalog.setval('public.dashboard_id_seq', 16, true);
SELECT pg_catalog.setval('public.tracks_id_seq', 195, true);
SELECT pg_catalog.setval('public.users_id_seq', 480, true);

ALTER TABLE ONLY public.poi
    ADD CONSTRAINT airfields_pkey PRIMARY KEY (name);
ALTER TABLE ONLY public.dashboard
    ADD CONSTRAINT dashboard_pkey PRIMARY KEY (id);
ALTER TABLE ONLY public.pass_recovery
    ADD CONSTRAINT pass_recovery_pkey PRIMARY KEY (token);
ALTER TABLE ONLY public.tracks
    ADD CONSTRAINT tracks_pkey PRIMARY KEY (id);
ALTER TABLE ONLY public.users
    ADD CONSTRAINT users_email_key UNIQUE (email);
ALTER TABLE ONLY public.users
    ADD CONSTRAINT users_pkey PRIMARY KEY (id);

CREATE INDEX airfields_location_gix ON public.poi USING gist (location);
CREATE INDEX tracks_track_gix ON public.tracks USING gist (track);

CREATE TRIGGER trigger_insert_tracks_hash BEFORE INSERT ON public.tracks FOR EACH ROW EXECUTE PROCEDURE public.generate_hash();
CREATE TRIGGER trigger_insert_users_token BEFORE INSERT ON public.users FOR EACH ROW EXECUTE PROCEDURE public.generate_token();

ALTER TABLE ONLY public.pass_recovery
    ADD CONSTRAINT pass_recovery_user_id_fkey FOREIGN KEY (user_id) REFERENCES public.users(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE ONLY public.tracks
    ADD CONSTRAINT tracks_user_id_fkey FOREIGN KEY (user_id) REFERENCES public.users(id) ON UPDATE CASCADE ON DELETE CASCADE;
